catalog_script_client.script:
function onLoad() {
    var btn = document.createElement("button");
    btn.innerHTML = "我的按钮";
    btn.className = "btn btn-default";
    btn.onclick = function() {
        // 按钮点击逻辑
        alert("按钮被点击!");
    };

    // 将按钮添加到特定元素
    var container = document.getElementById("some_element_id");
    if (container) {
        container.appendChild(btn);
    }
}
