catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var urlParams = new URLSearchParams(top.window.location.search);
    alert('urlParams=' + urlParams);
    var reqId = urlParams.get('reitem_id');
    if (reqId) {
        var ga = new GlideAjax('GetPreData');
        ga.addParam('sysparm_name', 'getPreviousRequestData');
        ga.addParam('sysparm_reqId', reqId);
        ga.getXMLAnswer(function(answer) {
            if (answer) {
                try {
                    var data = JSON.parse(answer);
                    console.log(data.reason_1, "111111");
                    console.log(data.reason_2, "111111");
                    if (data.approver) {
                        g_form.setValue('approver', data.approver);
                    }
                    if (data.reason_1) {
                        g_form.setValue('reason_1', data.reason_1);
                    }
                    if (data.reason_2) {
                        g_form.setValue('reason_2', data.reason_2);
                    }
                } catch (e) {}
            }
        });
    }
}
