sys_script_include.script:
var ExcelUpdateAjax = Class.create();
ExcelUpdateAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    parsingExcel: function() {
        try {
            var attachmentId = this.getParameter('sysparm_attachment_id');
            var formId = this.getParameter('sysparm_form_id');
            if (!attachmentId) return JSON.stringify([]);

            var gr = new GlideRecord('sys_attachment');
            if (!gr.get(attachmentId)) {
                return "ERROR: cant find the file";
            }
            var attachment = new GlideSysAttachment();
            var attachmentStream = attachment.getContentStream(attachmentId);
            var parser = new sn_impex.GlideExcelParser();
            parser.parse(attachmentStream);
            var headers = parser.getColumnHeaders();
            if (!headers || headers.length < 2) {
                return "ERROR: 2fileds need";
            }
            var result = [];
            while (parser.next()) {
                var row = parser.getRow();
                result.push({
                    'question1': row[headers[0]] + '',
                    'question2': row[headers[1]] + ''
                });
            }
            return JSON.stringify(result);
        } catch (e) {
            return "ERROR: " + e.message;
        }
    },
    type: 'ExcelUpdateAjax'
});
