sys_script_include.script:
var NeedModify = Class.create();
NeedModify.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    needmoreinfo: function() {
        var sysId = this.getParameter('sysparm_sys_id');
        var reqItem = new GlideRecord('sc_req_item');
        reqItem.get(sysId);
        reqItem.state = -10;
        reqItem.update();
        var task = new GlideRecord('sc_task');
        task.initialize();
        task.short_description = 'More Info for your Request: ';
        task.description = 'The request need more info. Please modify the details in below link: \n\n' +
            'https://dev339253.service-now.com/sp?id=sc_cat_item&sys_id=1452e9df8335221034af11b6feaad3ae&catitem_id=' + sysId;
        task.assigned_to = reqItem.requested_for;
        task.task_type = 'task';
        task.insert();
        return 'OK';
    },
    type: 'NeedModify'
});
