sys_script_include.script:
var ChangeReason = Class.create();
ChangeReason.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    returnTheItemforInof: function() {
        var sysId = this.getParameter('sysparm_sys_id');
        var reqItem = new GLideRecord('sc_req_item');
        reqItem.get(sysId);
        reqItem.state = 12;
        reqItem.update();
        var task = new GLideRecord('sc_task');
        task.initialize();
        task.short_description = 'More Info for your software Request: ';
        task.description = 'The request need more info. Please modify the details in below link: \n\n' +
            'https://dev346812.service-now.com/sp?id=sc_cat_item& sys_id=401305f1477de290e36952e1d16d4315&catitem_id=' + sysId;
        task.assigned_to = reqItem.requested_for;
        task.task_type = 'task';
        task.insert();
        return 'OK';
    },
    type: 'ChangeReason'
});
