sys_script_include.script:
var UpdateVariable = Class.create();
UpdateVariable.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    UpdateVariable: function() {
        try {
            // 获取参数
            var tableName = this.getParameter('sysparm_table');
            var comments = this.getParameter("sysparm_variable");
            var commentsValue = this.getParameter('sysparm_variable');
            // 验证输入
            if (!comments) {
                return {
                    error: true,
                    message: "缺少必要的参数"
                };
            }

            // 获取当前记录ID
            var sysId = this.getParameter('sysparm_sys_id');
            if (!sysId) {
                return "Error: Record ID is required";
            }

            // 更新记录
            var gr = new GlideRecord(tableName);
            if (gr.get(sysId)) {
                // 更新comments字段
                gr.setValue('comments', commentsValue);
                return gr.update() ? "success" : "Failed to update record";
            }
            return "Error: Record not found";
        } catch (e) {
            return "Error: " + e.message;
        }
    },
    type: 'UpdateVariable'
});
