sys_ui_page.client_script:
updateComments = function() {
    // 获取表名
    var tableName = g_form.getTableName();
    // 获取当前记录ID
    var sysId = g_form.getUniqueValue();
    // 获取更新的comments字段的值
	var newCommentsValue = document.getElementById('new-comments').value;

    // 发送 AJAX 请求
    var ga = new GlideAjax('UpdateVariable');
    // 调用Script Include方法
    ga.addParam('sysparm_name', 'UpdateVariable');
    ga.addParam('sysparm_sys_id', sysId);
    ga.addParam('sysparm_table', tableName);
    ga.addParam('sysparm_variable', newCommentsValue);

    ga.getXMLAnswer(function(response) {
        if (response === 'success') {
            // 更新画面上comments字段
            if (window.parent && window.parent.g_form) {
                g_form.setValue('x_1755581_demo3_for_scripting.comments', newCommentsValue);
            }
            GlideModal.get().destroy();
        } else {
            alert('Failed to update: ' + response);
        }
    });

};

window.closeDialog = function() {
    //Close the glide modal dialog window
    GlideModal.get().destroy();
};
